mapper.select:
		select * from sys_role
		
       		and role_name = #{role_name} 
       		and role_name like "%"#{role_nameL}"%" 
       		and role_sign = #{role_sign} 
       		and role_sign like "%"#{role_signL}"%" 
       	
       	 limit #{offset},#{limit}

		select * from sys_role 
		
       		and role_name = #{role_name} 
       		and role_sign = #{role_sign} 
       		and role_id = #{role_id} 
       		and role_id != #{role_id} 
       	
		 limit 1

	
		select count(role_id) from sys_role
		
       		and role_name = #{role_name} 
       		and role_name like "%"#{role_nameL}"%" 
       		and role_sign = #{role_sign} 
       		and role_sign like "%"#{role_signL}"%"

mapper.insert:
		delete from sys_role_menu where role_id = #{role_id};
		
		insert into sys_role_menu(role_id,menu_id) values 
		
    	(#{role_id},#{item})
